import React, { useState, useEffect } from 'react'
import styles from '../css/home.module.css'
import manJpg from '../icons/man.jpg'
import womanJpg from '../icons/woman.jpg'
import { useTransition, animated, config } from 'react-spring'

const Home = () => {

    const [Display, setDisplay] = useState(1);

    useEffect(() => {

        setTimeout(() => {
            Display === 1 ? setDisplay(2) : setDisplay(1);
        }, 6000);

    }, [Display])

    const transitions = useTransition(Display, Display, {
        from: { opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 }
    })

    return (
        <div>
            <animated.div style={transitions} className={styles['display']}>
                {Display === 1 &&
                    <div>
                        <div className={styles['display-left-col']}>
                            <div>
                                <h1 style={{ fontSize: '3rem', fontWeight: '500' }}>Catch Your Own <div style={{ fontWeight: 'bold' }}>Stylish Look</div></h1>
                                <p>A small inspirational designs taken from the niagra falls is the blue color which we implied into the jacket.</p>
                                <button>Discover Now</button>
                            </div>
                        </div>
                        <div className={styles['display-right-col']}><img src={manJpg} alt="man" /></div>
                    </div>
                }
                {Display === 2 &&
                    <div>
                        <div className={styles['display-left-col']}>
                            <div>
                                <h1 style={{ fontSize: '3rem', fontWeight: '500' }}>Catch Your Own <div style={{ fontWeight: 'bold' }}>Stylish Look</div></h1>
                                <p>Inspired from the Siberian snow, this our new snow white collection.</p>
                                <button>Shop Now</button>
                            </div>
                        </div>
                        <div className={styles['display-right-col']}><img src={womanJpg} alt="man" /></div>
                    </div>
                }
            </animated.div>
        </div>
    )
}

export default Home
