import React, { useState, useEffect } from 'react';
import Header from './Components/Header'
import Footer from './Components/Footer'
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Home from './Components/Home'
import Login from './Components/Login'
import Shop from './Components/Shop'
import Product from './Components/Product'
import UserContext from './Components/UserContext'
import PrivateRoute from './config/PrivateRoute'
import CartContext from './Components/CartContext'
import * as firebaseAuth from './config/FirebaseAuth'
import firebase from './config/Firebase'


function App() {
  const [User, setUser] = useState(null);
  const [Cart, setCart] = useState([]);
  const [Loading, setLoading] = useState(false)

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    const updation = async () => {
      const verificationResult = await firebaseAuth.verifySecuredToken(token);
      if (verificationResult !== null) {
        setUser({
          name: verificationResult.name,
          email: verificationResult.email,
          uid: verificationResult.uid
        });
      }
      if (verificationResult.cart === null) {
        const db = firebase.firestore();
        setCart([])
        db.collection('users').doc(verificationResult.uid).set({ cart: [] }, { merge: true })
      }
      console.log(Cart)
    }
    if (token) {
      setLoading(false)
      updation();
    } else {
      firebaseAuth.logout();
    }
  }, [])

  if (Loading) return <p>loading</p>

  return (
    <Router>
      <div>
        <UserContext.Provider value={{ User, setUser }}>
          <CartContext.Provider value={{ Cart, setCart }}>
            <Header />
            <Switch>
              <Route path="/" component={Home} exact />
              <Route path="/login" component={Login} exact />
              <PrivateRoute path="/shop" component={Shop} exact />
              <PrivateRoute path="/shop/:pid" component={Product} exact />
            </Switch>
            <Footer />
          </CartContext.Provider>
        </UserContext.Provider>
      </div>
    </Router>
  );
}

export default App;
